import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000', {
  transports: ['websocket'], // Force WebSocket transport only
});

function App() {
  const [username, setUsername] = useState('');
  const [usernameSubmitted, setUsernameSubmitted] = useState(false); // New state
  const [activeUsers, setActiveUsers] = useState([]);
  const [recipient, setRecipient] = useState('');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  const handleUsernameSubmit = () => {
    if (username.trim() !== '') {
      socket.emit('join', username);
      setUsernameSubmitted(true);
    }
  };

  useEffect(() => {
    socket.on('activeUsers', (users) => {
      setActiveUsers(users.filter((user) => user !== username));
    });

    socket.on('message', (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });
  }, [username]);

  const sendMessage = () => {
    if (recipient && message) {
      socket.emit('message', { recipient, message });
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: 'You', message: message },
      ]);
      setMessage('');
    }
  };

  return (
    <div>
      {!usernameSubmitted ? ( // Check if username has been submitted
        <div>
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button onClick={handleUsernameSubmit}>Set Username</button>
        </div>
      ) : (
        <div>
          <h2>Welcome, {username}!</h2>
          <h3>Active Users:</h3>
          <ul>
            {activeUsers.map((user) => (
              <li key={user}>{user}</li>
            ))}
          </ul>
          <h3>Select Recipient:</h3>
          <select
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
          >
            <option value="">Select User</option>
            {activeUsers.map((user) => (
              <option key={user} value={user}>
                {user}
              </option>
            ))}
          </select>
          <div>
            <input
              type="text"
              placeholder="Enter your message"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
          <h3>Chat Messages:</h3>
          <ul>
            {messages.map((msg, index) => (
              <li key={index}>
                <strong>{msg.sender}:</strong> {msg.message}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
