import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000', {
  transports: ['websocket'],
});

const Chat = () => {
  const [username, setUsername] = useState('');
  const [usernameSubmitted, setUsernameSubmitted] = useState(false);
  const [activeUsers, setActiveUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState({});

  useEffect(() => {
    socket.on('activeUsers', (users) => {
      setActiveUsers(users.filter((user) => user !== username));
    });
    socket.on('message', (data) => {
      setMessages((prevMessages) => {
        return {
          ...prevMessages,
          [data.sender]: [...(prevMessages[data.sender] || []), data.message],
        };
      });
    });

    return () => {
      socket.off('activeUsers');
      socket.off('message');
    };
  }, [username]);

  const handleUsernameSubmit = () => {
    if (username.trim() !== '') {
      socket.emit('join', username);
      setUsernameSubmitted(true);
    }
  };

  const sendMessage = () => {
    if (selectedUser && message) {
      socket.emit('message', { recipient: selectedUser, message });
      setMessage('');
      setMessages((prevMessages) => {
        return {
          ...prevMessages,
          [selectedUser]: [...(prevMessages[selectedUser] || []), message],
        };
      });
    }
  };

  const selectUser = (user) => {
    setSelectedUser(user);
  };

  if (!usernameSubmitted) {
    return (
      <div>
        <input
          type="text"
          placeholder="Enter your username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <button onClick={handleUsernameSubmit}>Set Username</button>
      </div>
    );
  }

  return (
    <div>
      <h2>Welcome, {username}!</h2>
      <h3>Active Users:</h3>
      <ul>
        {activeUsers.map((user) => (
          <li key={user} onClick={() => selectUser(user)} style={{ cursor: 'pointer' }}>
            {user}
          </li>
        ))}
      </ul>
      {selectedUser && (
        <div>
          <h3>Chat with {selectedUser}</h3>
          <input
            type="text"
            placeholder="Enter your message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button onClick={sendMessage}>Send</button>
          <ul>
            {messages[selectedUser] &&
              messages[selectedUser].map((msg, index) => (
                <li key={index}>
                  <strong>{selectedUser}:</strong> {msg}
                </li>
              ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Chat;













